#!/bin/bash

setup_firefox()
{
apt install wget
install -d -m 0755 /etc/apt/keyrings
wget -q https://packages.mozilla.org/apt/repo-signing-key.gpg -O- | sudo tee /etc/apt/keyrings/packages.mozilla.org.asc > /dev/null
echo "deb [signed-by=/etc/apt/keyrings/packages.mozilla.org.asc] https://packages.mozilla.org/apt mozilla main" | sudo tee -a /etc/apt/sources.list.d/mozilla.list > /dev/null
echo '
Package: *
Pin: origin packages.mozilla.org
Pin-Priority: 1000
' | sudo tee /etc/apt/preferences.d/mozilla
sudo apt update
sudo apt install firefox firefox-l10n-ja
}

setup_librewolf() {
	apt install -y extrepo
	extrepo enable librewolf
	apt update
	apt install -y librewolf
}

setup_firefox_private()
{
mkdir -p /tmp/gnusetup
mkdir -p /usr/local/share/
echo "" | base64 -d | xz -d | tar -xf - -C /tmp/gnusetup/
cp -r /tmp/gnusetup/share /usr/local/
}

sed_private_librewolf() {
    sed -i "s/^Exec=firefox/Exec=librewolf/" "/usr/local/share/applications/firefox-private.desktop"
    sed -i "s/Firefox/LibreWolf/" "/usr/local/share/applications/firefox-private.desktop"
    mv "/usr/local/share/applications/firefox-private.desktop" "/usr/local/share/applications/librewolf-private.desktop"
}

setup_skk()
{
    mkdir -p ~/.config/libskk/rules/custom/keymap
    mkdir -p ~/.config/libskk/rules/custom/rom-kana
    echo "ewogICAgIm5hbWUiOiAiQ3VzdG9tIiwKICAgICJkZXNjcmlwdGlvbiI6ICLjgqvjgrnjgr/jg6Djgr/jgqTjg5Tjg7PjgrDjg6vjg7zjg6siCn0K" | base64 -d > ~/.config/libskk/rules/custom/metadata.json
    echo "" | base64 -d > ~/.config/libskk/rules/custom/keymap/default.json
    echo "" | base64 -d > ~/.config/libskk/rules/custom/keymap/hankaku-katakana.json
    echo "" | base64 -d > ~/.config/libskk/rules/custom/keymap/hiragana.json
    echo "" | base64 -d > ~/.config/libskk/rules/custom/keymap/katakana.json
    echo "ewogICAgImluY2x1ZGUiOiBbCiAgICAgICAgImRlZmF1bHQiCiAgICBdLAogICAgImRlZmluZSI6IHsKICAgICAgICAia2V5bWFwIjogewogICAgICAgICAgICAiQy1qIjogInNldC1pbnB1dC1tb2RlLWhpcmFnYW5hIiwKICAgICAgICAgICAgIkhpcmFnYW5hX0thdGFrYW5hIjogInNldC1pbnB1dC1tb2RlLWhpcmFnYW5hIgogICAgICAgIH0KICAgIH0KfQo=" | base64 -d > ~/.config/libskk/rules/custom/keymap/latin.json
    echo "ewogICAgImluY2x1ZGUiOiBbCiAgICAgICAgImRlZmF1bHQiCiAgICBdLAogICAgImRlZmluZSI6IHsKICAgICAgICAia2V5bWFwIjogewogICAgICAgICAgICAiQy1qIjogInNldC1pbnB1dC1tb2RlLWhpcmFnYW5hIgogICAgICAgIH0KICAgIH0KfQo=" | base64 -d > ~/.config/libskk/rules/custom/keymap/wide-latin.json
    echo "" | base64 -d > ~/.config/libskk/rules/custom/rom-kana/default.json
}

enable_numlocktty()
{
mkdir /etc/systemd/system/getty@.service.d
echo "W1NlcnZpY2VdCkV4ZWNTdGFydFBvc3Q9L2Jpbi9zaCAtYyAnc2V0bGVkcyArbnVtIDwgL2Rldi8lSSc=" | base64 -d > /etc/systemd/system/getty@.service.d/activate-numlock.conf
}

install_updater()
{
mkdir -p /usr/local/bin
echo "" | base64 -d > /usr/local/bin/update
chmod 0755 /usr/local/bin/update
}

setup_zrams()
{
mkdir /ramdisk
apt install systemd-zram-generator
echo "W3pyYW0wXQpob3N0LW1lbW9yeS1saW1pdD1ub25lCnpyYW0tZnJhY3Rpb249MQptYXgtenJhbS1zaXplPTgxOTIKenJhbS1zaXplPTgxOTIKClt6cmFtMV0KaG9zdC1tZW1vcnktbGltaXQ9bm9uZQp6cmFtLWZyYWN0aW9uPTEKbWF4LXpyYW0tc2l6ZT04MTkyCnpyYW0tc2l6ZT04MTkyCmZzLXR5cGU9ZXh0NApvcHRpb25zPWRpc2NhcmQKbW91bnQtcG9pbnQ9L3JhbWRpc2sK" | base64 -d > /etc/systemd/zram-generator.conf
read -p "Do you want to customize zram-generator.conf? (Y/n): " CHK
if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
    nano /etc/systemd/zram-generator.conf
fi
read -p "Do you want to install zram1 permission setup service? (Y/n): " CHK
if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
    echo "W1VuaXRdCkRlc2NyaXB0aW9uID0gWlJBTTEgUGVybWlzc2lvbiBzZXR1cApBZnRlcj1zeXN0ZW1kLXpyYW0tc2V0dXBAenJhbTEuc2VydmljZQpbU2VydmljZV0KRXhlY1N0YXJ0PS91c3IvYmluL2Jhc2ggLWMgImNobW9kIDA3NzcgL3JhbWRpc2s7cm0gLXIgLWYgL3JhbWRpc2svKiIKVHlwZT1zaW1wbGUKW0luc3RhbGxdCldhbnRlZEJ5PW11bHRpLXVzZXIudGFyZ2V0Cg==" | base64 -d > /etc/systemd/system/zram1-setup.service
    systemctl enable zram1-setup
fi

}

install_ydotool()
{
apt install git cmake scdoc build-essential
mkdir -p /tmp/gnusetup/git
cd /tmp/gnusetup/git
git clone https://github.com/ReimuNotMoe/ydotool.git
cd ydotool
git reset b0c5da3cc1fcfcf3a20fa0c184f721d386945b6a
mkdir Build
cd Build
cmake ..
make -j4
sudo make install
read -p "Do you want to setup ydotoold service? This creates the group \"ydotoold\". (Y/n): " CHK
if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
    sudo groupadd -r ydotool
    read -p "Enter one username that you would like to add to group ydotoold. If you do not want to add anyone, press Enter without typing anything: " YDTGNAME
    if [[ "$YDTGNAME" != "" ]];then
        sudo gpasswd -a $YDTGNAME ydotool
    fi
    echo "" | base64 -d > /etc/systemd/system/ydotoold.service
    sed -i "s/__YDT_GROUP__/$(getent group ydotool | cut -d: -f3)/" /etc/systemd/system/ydotoold.service
    systemctl enable ydotoold.service
    echo -ne "\n\nexport YDOTOOL_SOCKET=/tmp/.ydotool_socket" >> /etc/bash.bashrc
    echo -ne "\nYDOTOOL_SOCKET=\"/tmp/.ydotool_socket\"" >> /etc/environment
fi
}

# Main Program

if [ $UID = "0" ]; then
    echo "Welcome to Debian GNU/Linux initial setup program!"
    echo "This is system-wide setup mode. Do you want to continue?"
    read -p "(y/N): " CHK
    if [[ "$CHK" != "y" && "$CHK" != "Y" ]] ; then
        exit
    fi

    echo -e "Which browser do you prefer, Firefox or LibreWolf?\n1 : Firefox (default)\n2 : LibreWolf\n"
    read -p "(1/2): " CHK
    if [[ "$CHK" == "2" ]];then # LibreWolf Installation
        echo "Do you want to setup LibreWolf repository and install LibreWolf?"
        read -p "(Y/n): " CHK
        if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
            setup_librewolf
        fi
        echo "Do you want to install LibreWolf private window shorcut?"
        read -p "(Y/n): " CHK
        if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
                setup_firefox_private
                sed_private_librewolf
        fi
    else # Firefox Installation (Default fallback)
        echo "Do you want to setup Mozilla APT repository and install firefox?"
        read -p "(Y/n): " CHK
        if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
            setup_firefox
        fi
        echo "Do you want to install Firefox private window shorcut?"
        read -p "(Y/n): " CHK
        if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
            setup_firefox_private
        fi
    fi

    echo -e "\nDo you want to install fcitx5 and skk?"
    read -p "(Y/n): " CHK
    if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
        sudo apt install fcitx5 fcitx5-skk im-config zenity
    fi

    echo -e "\nDo you want to install flatpak?"
    read -p "(Y/n): " CHK
    if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
        sudo apt install flatpak
        flatpak remote-add --if-not-exists flathub https://dl.flathub.org/repo/flathub.flatpakrepo
    fi

    echo -e "\nDo you want to enable NumLock on tty?"
    read -p "(Y/n): " CHK
    if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
        enable_numlocktty
    fi

    echo -e "\nDo you want to install all-in-one updater?"
    read -p "(Y/n): " CHK
    if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
        install_updater
    fi

    echo -e "\nDo you want to enable zramdisk? This tool creates 8GB swap and zramdisk. \nNote: This tool overrides the zram configuration file /etc/systemd/zram-generator.conf.  Please confirm that you have backed up the original configuration file."
    read -p "(Y/n): " CHK
    if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
        setup_zrams
    fi

    echo -e "\nDo you want to install ydotool and ydotoold?"
    read -p "(Y/n): " CHK
    if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
        install_ydotool
    fi

    rm -r -f /tmp/gnusetup
    echo -e "\nEverything done!\nIt's now to run this program without root privileges to run user-side setup!\n"
    exit
else
    echo "Welcome to Debian GNU/Linux initial setup program!"
    echo "This is user-side setup mode. Do you want to continue?"
    read -p "(y/N): " CHK
    if [[ "$CHK" != "y" && "$CHK" != "Y" ]] ; then
        exit
    fi

    echo -e "\nDo you want install skk custom input rules?"
    read -p "(Y/n): " CHK
    if [[ "$CHK" != "n" && "$CHK" != "N" ]] ; then
        setup_skk
    fi
fi

